import { useEffect, useState } from 'react';
import { collection, query, where, getDocs } from 'firebase/firestore';
import { firestore, auth } from '../firebase-config';
import axios from 'axios';

const Chat = () => {
  const [conversations, setConversations] = useState([]);
  const [prompt, setPrompt] = useState('');
  const [generatedResponse, setGeneratedResponse] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchConversations = async () => {
      try {
        const user = auth.currentUser;
        if (!user) {
          console.error('No user logged in');
          setError('Please log in to view conversations');
          return;
        }

        const q = query(collection(firestore, 'conversations'), where('userId', '==', user.uid));
        const querySnapshot = await getDocs(q);
        const conversationsList = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setConversations(conversationsList);
      } catch (error) {
        console.error('Error fetching conversations:', error);
        setError('An error occurred while fetching conversations.');
      } finally {
        setLoading(false);
      }
    };

    fetchConversations();
  }, []);

  const handleGenerate = async () => {
    try {
      const token = await auth.currentUser.getIdToken();
      const response = await axios.post('http://localhost:3000/generate-text', 
        { prompt },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setGeneratedResponse(response.data.generated_text);
    } catch (error) {
      console.error('Error generating text:', error);
      setError('Failed to generate text. Please try again.');
    }
  };

  const renderConversations = () => {
    if (loading) {
      return <p>Loading conversations...</p>;
    }

    if (conversations.length === 0) {
      return <p className="text-gray-500 italic">No conversations yet. Start chatting to create some!</p>;
    }

    return (
      <ul>
        {conversations.map(convo => (
          <li key={convo.id} className="mb-2 p-2 bg-gray-200 rounded">{convo.prompt}</li>
        ))}
      </ul>
    );
  };

  return (
    <div className="flex h-screen">
      <div className="w-1/3 p-4 bg-gray-100 overflow-y-auto">
        <h2 className="text-xl mb-4 font-bold">Conversation History</h2>
        {renderConversations()}
      </div>
      <div className="w-2/3 p-4 flex flex-col">
        <textarea
          value={prompt}
          onChange={e => setPrompt(e.target.value)}
          className="w-full p-2 border border-gray-300 rounded mb-4 flex-grow"
          placeholder="Type your prompt here..."
        />
        <button 
          onClick={handleGenerate} 
          className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition duration-200"
        >
          Generate Text
        </button>
        {error && <p className="text-red-500 mt-2">{error}</p>}
        {generatedResponse && (
          <div className="mt-4 p-4 bg-gray-100 rounded">
            <h3 className="text-xl font-bold mb-2">Generated Response:</h3>
            <p>{generatedResponse}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Chat;
