import { useState, useEffect } from 'react';
import { Send, Image, Loader2, Menu, X } from 'lucide-react';
import { collection, query, where, getDocs, addDoc, orderBy, serverTimestamp, updateDoc, doc } from 'firebase/firestore';
import { firestore, auth } from '../firebase-config';
import { useNavigate } from 'react-router-dom';
import AIAgent from '../AIAgent';

const platforms = ['LinkedIn', 'Twitter', 'Facebook', 'Instagram'];

export default function Chat() {
  const [platform, setPlatform] = useState('LinkedIn');
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [conversations, setConversations] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [error, setError] = useState(null);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [conversationHistory, setConversationHistory] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(user => {
      if (user) {
        console.log('User authenticated:', user.uid);
        fetchConversations();
      } else {
        console.log('No user authenticated, redirecting to login');
        navigate('/');
      }
    });

    return () => unsubscribe();
  }, [navigate]);

  const handleSubmit = async (type) => {
    if (!input.trim()) return;

    setIsLoading(true);
    setError(null);
    
    const newUserMessage = { type: 'user', content: input, platform };
    const updatedMessages = [...messages, newUserMessage];
    setMessages(updatedMessages);
    setInput('');

    try {
      const aiResponse = await AIAgent.processUserInput(input, platform);
      
      const newAssistantMessage = {
        type: 'assistant',
        content: aiResponse,
        platform
      };
      
      setMessages([...updatedMessages, newAssistantMessage]);

      // Update conversation history and save to Firestore
      const updatedHistory = [...conversationHistory, newUserMessage, newAssistantMessage];
      setConversationHistory(updatedHistory);

      // Save conversation to Firestore
      if (auth.currentUser?.uid) {
        console.log('Saving conversation to Firestore');
        try {
          const conversationData = {
            userId: auth.currentUser.uid,
            messages: [...updatedMessages, newAssistantMessage],
            platform,
            lastUpdated: serverTimestamp(),
            conversationHistory: updatedHistory
          };

          if (selectedConversation) {
            await updateDoc(doc(firestore, 'conversations', selectedConversation.id), conversationData);
            console.log('Conversation updated with ID:', selectedConversation.id);
          } else {
            const docRef = await addDoc(collection(firestore, 'conversations'), {
              ...conversationData,
              createdAt: serverTimestamp()
            });
            console.log('New conversation saved with ID:', docRef.id);
            setSelectedConversation({ id: docRef.id, ...conversationData });
          }
        } catch (firestoreError) {
          console.error('Error saving to Firestore:', firestoreError);
          setError('Failed to save conversation. Please try again.');
        }
      } else {
        console.error('User not authenticated');
        setError('User not authenticated. Please log in again.');
      }

      fetchConversations();
    } catch (err) {
      console.error('Error:', err);
      setError('An error occurred while processing your request. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-50 w-64 bg-white shadow-lg transform ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 ease-in-out md:relative md:translate-x-0`}>
        <div className="flex justify-between items-center p-4 border-b">
          <h2 className="text-xl font-semibold">Conversations</h2>
          <button onClick={() => setIsSidebarOpen(false)} className="md:hidden">
            <X className="w-6 h-6" />
          </button>
        </div>
        <div className="p-4">
          <button
            onClick={startNewConversation}
            className="w-full py-2 px-4 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition duration-300"
          >
            New Conversation
          </button>
        </div>
        <div className="overflow-y-auto h-full pb-20">
          {conversations.length === 0 ? (
            <p className="text-gray-500 text-center p-4">No conversations yet</p>
          ) : (
            conversations.map((conv) => (
              <div
                key={conv.id}
                onClick={() => loadConversation(conv)}
                className="p-4 border-b hover:bg-gray-100 cursor-pointer transition duration-300"
              >
                <p className="font-medium truncate">
                  {conv.prompt || (conv.messages && conv.messages.length > 0 ? conv.messages[0].content : 'No messages')}
                </p>
                <p className="text-sm text-gray-500">
                  {conv.createdAt ? conv.createdAt.toLocaleString() : 'Unknown date'}
                </p>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Main Content */}
      <div className="flex flex-col flex-1 overflow-hidden">
        {/* Header */}
        <header className="bg-white shadow-sm p-4 flex justify-between items-center">
          <button onClick={() => setIsSidebarOpen(true)} className="md:hidden">
            <Menu className="w-6 h-6" />
          </button>
          <h1 className="text-2xl font-bold text-center flex-1">
            {selectedConversation ? `Conversation from ${selectedConversation.createdAt.toLocaleString()}` : 'Social Media Post Generator'}
          </h1>
          {selectedConversation && (
            <button
              onClick={startNewConversation}
              className="bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600 transition duration-300"
            >
              New Conversation
            </button>
          )}
        </header>

        {/* Main chat area */}
        <div className="flex-1 overflow-y-auto p-4 space-y-4">
          {messages.map((message, index) => (
            <div key={index} className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}>
              <div className={`max-w-[70%] rounded-lg p-3 ${message.type === 'user' ? 'bg-blue-500 text-white' : 'bg-white shadow'}`}>
                {message.platform && <p className="font-semibold mb-1">{message.platform}</p>}
                <p>{message.content}</p>
                {message.image && <img src={message.image} alt="Generated" className="mt-2 rounded-lg max-w-full h-auto" />}
              </div>
            </div>
          ))}
        </div>

        {/* Input area */}
        <div className="bg-white border-t border-gray-200 p-4">
          <div className="flex space-x-2 mb-2 overflow-x-auto pb-2">
            {platforms.map(p => (
              <button
                key={p}
                onClick={() => setPlatform(p)}
                className={`px-3 py-1 rounded-full text-sm flex-shrink-0 ${
                  platform === p ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'
                }`}
              >
                {p}
              </button>
            ))}
          </div>
          <div className="flex items-center space-x-2">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type your prompt here..."
              className="flex-1 p-2 border rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500"
              onKeyPress={(e) => e.key === 'Enter' && handleSubmit('text')}
            />
            <button
              onClick={() => handleSubmit('text')}
              disabled={isLoading}
              className="bg-blue-500 text-white p-2 rounded-full hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              {isLoading ? <Loader2 className="w-5 h-5 animate-spin" /> : <Send className="w-5 h-5" />}
            </button>
            <button
              onClick={() => handleSubmit('image')}
              disabled={isLoading}
              className="bg-purple-500 text-white p-2 rounded-full hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-purple-500"
            >
              {isLoading ? <Loader2 className="w-5 h-5 animate-spin" /> : <Image className="w-5 h-5" />}
            </button>
          </div>
        </div>
      </div>
      {error && <div className="text-red-500 p-4 absolute bottom-0 left-0 right-0 bg-white">{error}</div>}
    </div>
  );
}
