import axios from './axiosConfig';
import { auth } from './firebase-config';

class AIAgent {
  constructor() {
    this.conversationHistory = [];
    this.currentStage = 'greeting';
    this.isLongResponseRequested = false;
    this.requestedWordCount = 0;
  }

  async processUserInput(userInput, platform, conversationId) {
    this.conversationHistory.push({ role: 'user', content: userInput });

    const prompt = this.constructPrompt(platform, userInput);

    try {
      const token = await auth.currentUser?.getIdToken();
      const response = await axios.post('/generate-text', 
        { 
          platform, 
          prompt: prompt, // Send the full prompt, not just userInput
          conversationId,
          wordCount: 50 // Limit to 50 words for conciseness
        },
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      let aiResponse = response.data.generated_text;
      aiResponse = this.ensureCompleteResponse(aiResponse);
      aiResponse = this.validateResponse(aiResponse, userInput, platform);
      aiResponse = this.enforceRoleAndContext(aiResponse, platform);

      this.conversationHistory.push({ role: 'assistant', content: aiResponse });
      this.updateConversationStage(aiResponse, userInput);

      return {
        text: aiResponse,
        conversationId: response.data.conversationId
      };
    } catch (error) {
      console.error('Error in processing:', error);
      throw error;
    }
  }

  enforceRoleAndContext(response, platform) {
    const intro = `As a professional Content Strategist for ${platform}, here's my advice: `;
    if (!response.startsWith(intro)) {
      response = intro + response;
    }
    return response;
  }

  ensureCompleteResponse(response) {
    // Split the response into sentences
    const sentences = response.match(/[^.!?]+[.!?]+/g) || [];
    
    // Join complete sentences
    let completeResponse = sentences.join(' ').trim();
    
    // If the response doesn't end with punctuation, add a period
    if (!/[.!?]$/.test(completeResponse)) {
      completeResponse += '.';
    }
    
    return completeResponse;
  }

  validateResponse(response, userInput, platform) {
    // Check if the response is relevant to the user's input and the platform
    if (!this.isRelevantResponse(response, userInput, platform)) {
      return `I apologize, but I may have misunderstood your question about "${userInput}". As a ${platform} Content Strategist, could you please clarify or rephrase your question? I'm here to help with ${platform}-related content.`;
    }
    
    // Ensure the response is not too long
    const words = response.split(/\s+/);
    if (words.length > 50) {
      return words.slice(0, 50).join(' ') + '...';
    }
    
    return response;
  }

  isRelevantResponse(response, userInput, platform) {
    const relevantKeywords = [
      ...userInput.toLowerCase().split(/\s+/),
      platform.toLowerCase(),
      'post',
      'content',
      'social media'
    ];
    
    const responseWords = response.toLowerCase().split(/\s+/);
    return relevantKeywords.some(keyword => responseWords.includes(keyword));
  }

  constructPrompt(platform, userInput) {
    return `You are Sam, a professional Content Strategist specializing in ${platform}. Your role is to help users craft engaging social media posts. Respond concisely (max 50 words) in a friendly, professional tone. Provide specific, actionable advice for ${platform}. Do not use emojis or mention that you're an AI.

Key Points:
1. Always respond as Sam, the Content Strategist for ${platform}.
2. Keep responses brief, concise, and directly related to the user's input.
3. Use the 7 C's of communication: Clear, Concise, Concrete, Correct, Coherent, Complete, and Courteous.
4. Analyze the user's input and provide expert insights specific to ${platform}.
5. Ensure all responses are complete and not cut off mid-sentence.
6. Offer platform-specific strategies.
7. Suggest relevant hashtags or trends when appropriate.
8. Maintain a balance between professionalism and approachability.
9. After creating the post, ask if the user wants an image.
10. Ensure all responses are complete and not cut off mid-sentence or mid-point.

Current stage: ${this.currentStage}.

Conversation history:
${this.conversationHistory.map(msg => `${msg.role}: ${msg.content}`).join('\n')}

Based on this, provide a brief, helpful response as Sam, the ${platform} Content Strategist. Ensure your advice is specific to ${platform} and actionable.

User's input: "${userInput}"

Sam: `;
  }

  constructContinuationPrompt(platform, incompleteResponse) {
    return `Continue the following response for a ${platform} post, ensuring it's complete:

${incompleteResponse}

Continuation: `;
  }

  extractRelevantContent(fullResponse) {
    const assistantIndex = fullResponse.indexOf("Sam:");
    if (assistantIndex !== -1) {
      fullResponse = fullResponse.substring(assistantIndex + "Sam:".length);
    }

    fullResponse = fullResponse.replace(/^[\s\n]*/, '');
    fullResponse = fullResponse.replace(/\n*$/, '');

    return fullResponse.trim();
  }

  isResponseComplete(response) {
    // Check if the response ends with a punctuation mark
    const endsWithPunctuation = /[.!?]$/.test(response);
    
    // Check if there are any incomplete numbered lists
    const hasIncompleteList = /\d+\.(?![^\n]*\n\d+\.)/.test(response);

    return endsWithPunctuation && !hasIncompleteList;
  }

  updateConversationStage(aiResponse, userInput) {
    switch (this.currentStage) {
      case 'greeting':
        if (aiResponse.includes("What's the main topic or theme")) {
          this.currentStage = 'topic_selection';
        }
        break;
      case 'topic_selection':
        if (aiResponse.includes("Who is your target audience")) {
          this.currentStage = 'audience_definition';
        }
        break;
      case 'audience_definition':
        if (aiResponse.includes("What's the main goal")) {
          this.currentStage = 'goal_setting';
        }
        break;
      case 'goal_setting':
        if (aiResponse.includes("Let's brainstorm some ideas")) {
          this.currentStage = 'content_ideation';
        }
        break;
      case 'content_ideation':
        if (aiResponse.includes("Great ideas! Now, let's craft")) {
          this.currentStage = 'content_creation';
        }
        break;
      case 'content_creation':
        if (aiResponse.includes("Here's a draft of your post")) {
          this.currentStage = 'review_and_refine';
        }
        break;
      case 'review_and_refine':
        if (aiResponse.includes("Would you like to generate an image")) {
          this.currentStage = 'image_generation_query';
        }
        break;
      case 'image_generation_query':
        if (aiResponse.includes("Great! Let's generate an image") || aiResponse.includes("No problem, we'll skip the image")) {
          this.currentStage = 'final_touches';
        }
        break;
      case 'final_touches':
        if (aiResponse.includes("Your post is now ready to publish")) {
          this.currentStage = 'completion';
        }
        break;
      default:
        break;
    }
  }

  resetConversation() {
    this.conversationHistory = [];
    this.currentStage = 'greeting';
  }
}

const aiAgent = new AIAgent();
export default aiAgent;
