import { useState, useEffect } from 'react';
import { Send, Image, Loader2, Menu, X } from 'lucide-react';
import { collection, query, where, getDocs, addDoc, orderBy, serverTimestamp, updateDoc, doc } from 'firebase/firestore';
import { firestore, auth } from '../firebase-config';
import axios from '../axiosConfig';
import { useNavigate } from 'react-router-dom';

const platforms = ['LinkedIn', 'Twitter', 'Facebook', 'Instagram'];

export default function Chat() {
  const [platform, setPlatform] = useState('LinkedIn');
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [conversations, setConversations] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [error, setError] = useState(null);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [conversationHistory, setConversationHistory] = useState([]);
  const navigate = useNavigate();
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [userResponses, setUserResponses] = useState({});

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(user => {
      if (user) {
        console.log('User authenticated:', user.uid);
        fetchConversations();
      } else {
        console.log('No user authenticated, redirecting to login');
        navigate('/');
      }
    });

    return () => unsubscribe();
  }, [navigate]);

  const fetchConversations = async () => {
    setError(null);
    try {
      const user = auth.currentUser;
      if (!user) throw new Error('No user logged in');

      console.log('Fetching conversations for user:', user.uid);
      const q = query(
        collection(firestore, 'conversations'),
        where('userId', '==', user.uid),
        orderBy('createdAt', 'desc')
      );
      const querySnapshot = await getDocs(q);
      const conversationsList = querySnapshot.docs.map(doc => {
        const data = doc.data();
        return {
          id: doc.id,
          prompt: data.prompt,
          response: data.response,
          platform: data.platform,
          createdAt: data.createdAt ? data.createdAt.toDate() : new Date(),
          messages: [
            { type: 'user', content: data.prompt, platform: data.platform },
            { type: 'assistant', content: data.response, platform: data.platform }
          ]
        };
      });
      console.log('Fetched conversations:', conversationsList);
      setConversations(conversationsList);
    } catch (err) {
      console.error('Error fetching conversations:', err);
      if (err.code === 'failed-precondition' || err.message.includes('requires an index')) {
        setError('Database index is being created. Please try again in a few minutes.');
      } else {
        setError('Failed to fetch conversations. Please try again.');
      }
    }
  };

  const handleSubmit = async (type) => {
    if (!input.trim()) return;

    setIsLoading(true);
    setError(null);
    
    const newUserMessage = { type: 'user', content: input, platform };
    const updatedMessages = [...messages, newUserMessage];
    setMessages(updatedMessages);
    setInput('');

    if (currentQuestion) {
      // Handle user's response to AI's question
      setUserResponses(prev => ({ ...prev, [currentQuestion.key]: input }));
      await askNextQuestion(updatedMessages);
    } else {
      // Initial user input, start the questioning process
      await askNextQuestion(updatedMessages);
    }
  };

  const askNextQuestion = async (updatedMessages) => {
    const questions = [
      { key: 'tone', question: 'What tone do you prefer for this post?', options: ['Professional', 'Inspirational', 'Casual'] },
      { key: 'length', question: 'How long should the post be?', options: ['Short (1-2 sentences)', 'Medium (3-4 sentences)', 'Long (5+ sentences)'] },
      { key: 'goal', question: 'What\'s the main goal of this post?', options: ['Inform', 'Engage', 'Promote'] },
      { key: 'callToAction', question: 'Do you want to include a call-to-action?', options: ['Yes', 'No'] },
    ];

    const nextQuestion = questions.find(q => !userResponses[q.key]);

    if (nextQuestion) {
      setCurrentQuestion(nextQuestion);
      const aiMessage = {
        type: 'assistant',
        content: `${nextQuestion.question} ${nextQuestion.options.join(', ')}`,
        platform
      };
      updatedMessages.push(aiMessage);
      setMessages(updatedMessages);
      setIsLoading(false);
    } else {
      // All questions answered, generate the final post
      await generateFinalPost(updatedMessages);
    }
  };

  const generateFinalPost = async (updatedMessages) => {
    let enhancedPrompt = constructEnhancedPrompt(userResponses, platform, 'text', conversationHistory);
    
    try {
      const token = await auth.currentUser?.getIdToken();
      console.log('Sending request to backend');
      const response = await axios.post('/generate-text', 
        { platform, prompt: enhancedPrompt },
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );
      
      const data = response.data;
      
      console.log('AI Model Response:', data);
      
      // Extract only the generated content, removing any instructions or prompts
      const aiContent = extractGeneratedContent(data.generated_text);
      
      const newAssistantMessage = {
        type: 'assistant',
        content: aiContent,
        platform
      };
      
      updatedMessages.push(newAssistantMessage);
      setMessages(updatedMessages);

      // Update conversation history
      const updatedHistory = [...conversationHistory, ...updatedMessages];
      setConversationHistory(updatedHistory);

      // Save conversation to Firestore
      if (auth.currentUser?.uid) {
        console.log('Saving conversation to Firestore');
        try {
          const conversationData = {
            userId: auth.currentUser.uid,
            messages: updatedMessages,
            platform,
            lastUpdated: serverTimestamp(),
            conversationHistory: updatedHistory
          };

          if (selectedConversation) {
            await updateDoc(doc(firestore, 'conversations', selectedConversation.id), conversationData);
            console.log('Conversation updated with ID:', selectedConversation.id);
          } else {
            const docRef = await addDoc(collection(firestore, 'conversations'), {
              ...conversationData,
              createdAt: serverTimestamp()
            });
            console.log('New conversation saved with ID:', docRef.id);
            setSelectedConversation({ id: docRef.id, ...conversationData });
          }
        } catch (firestoreError) {
          console.error('Error saving to Firestore:', firestoreError);
          setError('Failed to save conversation. Please try again.');
        }
      } else {
        console.error('User not authenticated');
        setError('User not authenticated. Please log in again.');
      }

      fetchConversations();
    } catch (err) {
      console.error('Error:', err);
      let errorMessage = 'An error occurred while generating content. Please try again.';
      if (err.response && err.response.data && err.response.data.error) {
        errorMessage = err.response.data.error;
        if (err.response.data.details) {
          errorMessage += ` Details: ${err.response.data.details}`;
        }
      }
      setError(errorMessage);
    } finally {
      setIsLoading(false);
      setCurrentQuestion(null);
      setUserResponses({});
    }
  };

  const constructEnhancedPrompt = (userResponses, platform, type, history) => {
    let basePrompt = `Generate a ${type === 'text' ? 'post' : 'image concept'} for ${platform} based on the following preferences: `;

    basePrompt += `Tone: ${userResponses.tone}, `;
    basePrompt += `Length: ${userResponses.length}, `;
    basePrompt += `Goal: ${userResponses.goal}, `;
    basePrompt += `Include Call-to-Action: ${userResponses.callToAction}. `;

    // Add platform-specific instructions
    switch (platform) {
      case 'LinkedIn':
        basePrompt += `Use a professional tone, focus on business content, include industry hashtags. `;
        break;
      case 'Twitter':
        basePrompt += `Be concise, impactful, use hashtags, under 280 characters. `;
        break;
      case 'Facebook':
        basePrompt += `Create engaging content, encourage interaction, consider rich media. `;
        break;
      case 'Instagram':
        basePrompt += `Create a visual caption, use emojis and hashtags, suggest platform features. `;
        break;
    }

    basePrompt += `Provide engagement strategies and 2-3 variations. `;

    // Add conversation history to the prompt
    if (history.length > 0) {
      basePrompt += `Consider the following conversation history: `;
      history.slice(-4).forEach((message, index) => {
        basePrompt += `${message.type === 'user' ? 'User' : 'Assistant'}: ${message.content} `;
      });
    }

    return basePrompt;
  };

  const startNewConversation = () => {
    setMessages([]);
    setSelectedConversation(null);
    setPlatform('LinkedIn');
    setIsSidebarOpen(false);
    setConversationHistory([]);
  };

  const loadConversation = (conversation) => {
    if (conversation && conversation.messages) {
      console.log('Loading conversation:', conversation);
      setMessages(conversation.messages);
      setPlatform(conversation.platform || 'LinkedIn');
      setSelectedConversation(conversation);
      setIsSidebarOpen(false);
      // Set conversation history
      setConversationHistory(conversation.conversationHistory || []);
    } else {
      console.error('Invalid conversation data:', conversation);
      setError('Failed to load conversation. Please try again.');
    }
  };

  // Helper function to extract only the generated content
  const extractGeneratedContent = (fullResponse) => {
    // Remove any text before "Generated Post" or similar indicators
    const contentStart = fullResponse.indexOf("Generated Post");
    if (contentStart !== -1) {
      fullResponse = fullResponse.substring(contentStart);
    }

    // Remove any text after engagement strategies or variations
    const contentEnd = fullResponse.indexOf("Engagement strategies:");
    if (contentEnd !== -1) {
      fullResponse = fullResponse.substring(0, contentEnd);
    }

    // Remove any remaining instructions or labels
    fullResponse = fullResponse.replace(/Generated Post.*?:/g, '').trim();
    fullResponse = fullResponse.replace(/Verification of the Content:.*$/s, '').trim();

    return fullResponse;
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-50 w-64 bg-white shadow-lg transform ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 ease-in-out md:relative md:translate-x-0`}>
        <div className="flex justify-between items-center p-4 border-b">
          <h2 className="text-xl font-semibold">Conversations</h2>
          <button onClick={() => setIsSidebarOpen(false)} className="md:hidden">
            <X className="w-6 h-6" />
          </button>
        </div>
        <div className="p-4">
          <button
            onClick={startNewConversation}
            className="w-full py-2 px-4 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition duration-300"
          >
            New Conversation
          </button>
        </div>
        <div className="overflow-y-auto h-full pb-20">
          {conversations.length === 0 ? (
            <p className="text-gray-500 text-center p-4">No conversations yet</p>
          ) : (
            conversations.map((conv) => (
              <div
                key={conv.id}
                onClick={() => loadConversation(conv)}
                className="p-4 border-b hover:bg-gray-100 cursor-pointer transition duration-300"
              >
                <p className="font-medium truncate">
                  {conv.prompt || (conv.messages && conv.messages.length > 0 ? conv.messages[0].content : 'No messages')}
                </p>
                <p className="text-sm text-gray-500">
                  {conv.createdAt ? conv.createdAt.toLocaleString() : 'Unknown date'}
                </p>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Main Content */}
      <div className="flex flex-col flex-1 overflow-hidden">
        {/* Header */}
        <header className="bg-white shadow-sm p-4 flex justify-between items-center">
          <button onClick={() => setIsSidebarOpen(true)} className="md:hidden">
            <Menu className="w-6 h-6" />
          </button>
          <h1 className="text-2xl font-bold text-center flex-1">
            {selectedConversation ? `Conversation from ${selectedConversation.createdAt.toLocaleString()}` : 'Social Media Post Generator'}
          </h1>
          {selectedConversation && (
            <button
              onClick={startNewConversation}
              className="bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600 transition duration-300"
            >
              New Conversation
            </button>
          )}
        </header>

        {/* Main chat area */}
        <div className="flex-1 overflow-y-auto p-4 space-y-4">
          {messages.map((message, index) => (
            <div key={index} className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}>
              <div className={`max-w-[70%] rounded-lg p-3 ${message.type === 'user' ? 'bg-blue-500 text-white' : 'bg-white shadow'}`}>
                {message.platform && <p className="font-semibold mb-1">{message.platform}</p>}
                <p>{message.content}</p>
                {message.image && <img src={message.image} alt="Generated" className="mt-2 rounded-lg max-w-full h-auto" />}
              </div>
            </div>
          ))}
        </div>

        {/* Input area */}
        <div className="bg-white border-t border-gray-200 p-4">
          <div className="flex space-x-2 mb-2 overflow-x-auto pb-2">
            {platforms.map(p => (
              <button
                key={p}
                onClick={() => setPlatform(p)}
                className={`px-3 py-1 rounded-full text-sm flex-shrink-0 ${
                  platform === p ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'
                }`}
              >
                {p}
              </button>
            ))}
          </div>
          <div className="flex items-center space-x-2">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type your prompt here..."
              className="flex-1 p-2 border rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500"
              onKeyPress={(e) => e.key === 'Enter' && handleSubmit('text')}
            />
            <button
              onClick={() => handleSubmit('text')}
              disabled={isLoading}
              className="bg-blue-500 text-white p-2 rounded-full hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              {isLoading ? <Loader2 className="w-5 h-5 animate-spin" /> : <Send className="w-5 h-5" />}
            </button>
            <button
              onClick={() => handleSubmit('image')}
              disabled={isLoading}
              className="bg-purple-500 text-white p-2 rounded-full hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-purple-500"
            >
              {isLoading ? <Loader2 className="w-5 h-5 animate-spin" /> : <Image className="w-5 h-5" />}
            </button>
          </div>
        </div>
      </div>
      {error && <div className="text-red-500 p-4 absolute bottom-0 left-0 right-0 bg-white">{error}</div>}
    </div>
  );
}
