import axios from '../axiosConfig';

class AIAgent {
  constructor() {
    this.conversationHistory = [];
  }

  async processUserInput(userInput, platform) {
    this.conversationHistory.push({ role: 'user', content: userInput });

    const prompt = this.constructPrompt(platform);

    try {
      const response = await axios.post('/generate-text', 
        { platform, prompt },
        { 
          headers: { 
            Authorization: `Bearer ${await auth.currentUser?.getIdToken()}`,
            'Content-Type': 'application/json',
          },
        }
      );

      const aiResponse = response.data.generated_text;
      this.conversationHistory.push({ role: 'assistant', content: aiResponse });

      return aiResponse;
    } catch (error) {
      console.error('Error in AI processing:', error);
      throw error;
    }
  }

  constructPrompt(platform) {
    let prompt = `You are a viral content creator and AI assistant helping to create a ${platform} post. `;
    prompt += `Here's the conversation history:\n\n`;

    this.conversationHistory.forEach(message => {
      prompt += `${message.role}: ${message.content}\n`;
    });

    prompt += `\nBased on this conversation, provide a helpful response. If the user is asking to generate or modify a post, do so. If they're asking questions, answer them. Always maintain a friendly and professional tone.`;

    return prompt;
  }

  resetConversation() {
    this.conversationHistory = [];
  }
}

export default new AIAgent();
