import axios from './axiosConfig';
import { auth } from './firebase-config';

class AIAgent {
  constructor() {
    this.conversationHistory = [];
    this.currentStage = 'greeting';
  }

  async processUserInput(userInput, platform, conversationId) {
    this.conversationHistory.push({ role: 'user', content: userInput });

    const prompt = this.constructPrompt(platform, userInput);

    try {
      const token = await auth.currentUser?.getIdToken();
      const response = await axios.post('/generate-text', 
        { platform, prompt, conversationId },
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      let aiResponse = this.extractRelevantContent(response.data.generated_text);
      
      // Ensure the response is complete
      while (!this.isResponseComplete(aiResponse)) {
        const continuationPrompt = this.constructContinuationPrompt(platform, aiResponse);
        const continuationResponse = await axios.post('/generate-text', 
          { platform, prompt: continuationPrompt, conversationId },
          { 
            headers: { 
              Authorization: `Bearer ${token}`,
              'Content-Type': 'application/json',
            },
          }
        );
        const continuation = this.extractRelevantContent(continuationResponse.data.generated_text);
        aiResponse += ' ' + continuation;
      }

      this.conversationHistory.push({ role: 'assistant', content: aiResponse });

      this.updateConversationStage(aiResponse, userInput);

      return {
        text: aiResponse,
        conversationId: response.data.conversationId
      };
    } catch (error) {
      console.error('Error in processing:', error);
      throw error;
    }
  }

  constructPrompt(platform, userInput) {
    let prompt = `You are Sam, a professional Content Strategist specializing in ${platform}. Your role is to help users craft engaging social media posts. Respond concisely in a friendly, professional tone. Provide specific, actionable advice for ${platform}. Do not use emojis or mention that you're an AI.

Key Points:
1. Ask one brief question at a time and wait for the user's answer before proceeding.
2. Analyze the user's input and provide expert insights.
3. Offer concise, platform-specific strategies.
4. Suggest relevant hashtags or trends when appropriate.
5. Maintain a balance between professionalism and approachability.
6. After creating the post, ask if the user wants an image.
7. Ensure all responses are complete and not cut off mid-sentence or mid-point.

Current stage: ${this.currentStage}.

Conversation history:
${this.conversationHistory.map(msg => `${msg.role}: ${msg.content}`).join('\n')}

Based on this, provide a brief, helpful response. Ensure your advice is specific to ${platform} and actionable. Your response must be complete and not cut off.

User's input: "${userInput}"

Sam: `;

    return prompt;
  }

  constructContinuationPrompt(platform, incompleteResponse) {
    return `Continue the following response for a ${platform} post, ensuring it's complete:

${incompleteResponse}

Continuation: `;
  }

  extractRelevantContent(fullResponse) {
    const assistantIndex = fullResponse.indexOf("Sam:");
    if (assistantIndex !== -1) {
      fullResponse = fullResponse.substring(assistantIndex + "Sam:".length);
    }

    fullResponse = fullResponse.replace(/^[\s\n]*/, '');
    fullResponse = fullResponse.replace(/\n*$/, '');

    return fullResponse.trim();
  }

  isResponseComplete(response) {
    // Check if the response ends with a punctuation mark
    const endsWithPunctuation = /[.!?]$/.test(response);
    
    // Check if there are any incomplete numbered lists
    const hasIncompleteList = /\d+\.(?![^\n]*\n\d+\.)/.test(response);

    return endsWithPunctuation && !hasIncompleteList;
  }

  updateConversationStage(aiResponse, userInput) {
    switch (this.currentStage) {
      case 'greeting':
        if (aiResponse.includes("What's the main topic or theme")) {
          this.currentStage = 'topic_selection';
        }
        break;
      case 'topic_selection':
        if (aiResponse.includes("Who is your target audience")) {
          this.currentStage = 'audience_definition';
        }
        break;
      case 'audience_definition':
        if (aiResponse.includes("What's the main goal")) {
          this.currentStage = 'goal_setting';
        }
        break;
      case 'goal_setting':
        if (aiResponse.includes("Let's brainstorm some ideas")) {
          this.currentStage = 'content_ideation';
        }
        break;
      case 'content_ideation':
        if (aiResponse.includes("Great ideas! Now, let's craft")) {
          this.currentStage = 'content_creation';
        }
        break;
      case 'content_creation':
        if (aiResponse.includes("Here's a draft of your post")) {
          this.currentStage = 'review_and_refine';
        }
        break;
      case 'review_and_refine':
        if (aiResponse.includes("Would you like to generate an image")) {
          this.currentStage = 'image_generation_query';
        }
        break;
      case 'image_generation_query':
        if (aiResponse.includes("Great! Let's generate an image") || aiResponse.includes("No problem, we'll skip the image")) {
          this.currentStage = 'final_touches';
        }
        break;
      case 'final_touches':
        if (aiResponse.includes("Your post is now ready to publish")) {
          this.currentStage = 'completion';
        }
        break;
      default:
        break;
    }
  }

  resetConversation() {
    this.conversationHistory = [];
    this.currentStage = 'greeting';
  }
}

const aiAgent = new AIAgent();
export default aiAgent;
