import { useEffect, useState } from 'react';
import { collection, query, where, getDocs } from 'firebase/firestore';
import { firestore, auth } from '../firebase-config';
import axios from 'axios';

const Chat = () => {
  const [conversations, setConversations] = useState([]);
  const [prompt, setPrompt] = useState('');
  const [generatedResponse, setGeneratedResponse] = useState('');
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchConversations = async () => {
      try {
        const user = auth.currentUser;
        if (!user) {
          console.error('No user logged in');
          setError('Please log in to view conversations');
          return;
        }

        const q = query(collection(firestore, 'conversations'), where('userId', '==', user.uid));
        const querySnapshot = await getDocs(q);
        const conversationsList = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setConversations(conversationsList);
      } catch (error) {
        console.error('Error fetching conversations:', error);
        setError('Failed to fetch conversations. Please try again.');
      }
    };

    fetchConversations();
  }, []);

  const handleGenerate = async () => {
    try {
      const token = await auth.currentUser.getIdToken();
      const response = await axios.post('http://localhost:3000/generate-text', 
        { prompt },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setGeneratedResponse(response.data.generated_text);
    } catch (error) {
      console.error('Error generating text:', error);
      setError('Failed to generate text. Please try again.');
    }
  };

  if (error) {
    return <div className="text-red-500">{error}</div>;
  }

  return (
    <div className="flex">
      <div className="w-1/3 p-4 bg-gray-100">
        <h2 className="text-xl mb-4">Conversation History</h2>
        <ul>
          {conversations.map(convo => (
            <li key={convo.id}>{convo.prompt}</li>
          ))}
        </ul>
      </div>
      <div className="w-2/3 p-4">
        <textarea
          value={prompt}
          onChange={e => setPrompt(e.target.value)}
          className="w-full p-2 border border-gray-300 rounded mb-4"
          placeholder="Type your prompt here..."
        />
        <button onClick={handleGenerate} className="bg-blue-500 text-white py-2 px-4 rounded">
          Generate Text
        </button>
        <div className="mt-4">
          <h3 className="text-xl">Generated Response:</h3>
          <p>{generatedResponse}</p>
        </div>
      </div>
    </div>
  );
};

export default Chat;
