import { useState, useEffect } from 'react';
import { Send, Image, Loader2, Menu, X } from 'lucide-react';
import { collection, query, where, getDocs, addDoc, orderBy } from 'firebase/firestore';
import { firestore, auth } from '../firebase-config';
import axios from '../axiosConfig';
import { useNavigate } from 'react-router-dom';

const platforms = ['LinkedIn', 'Twitter', 'Facebook', 'Instagram'];

export default function Chat() {
  const [platform, setPlatform] = useState('LinkedIn');
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [conversations, setConversations] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(user => {
      if (user) {
        fetchConversations();
      } else {
        navigate('/');
      }
    });

    return () => unsubscribe();
  }, [navigate]);

  const fetchConversations = async () => {
    setError(null);
    try {
      const user = auth.currentUser;
      if (!user) throw new Error('No user logged in');

      const q = query(
        collection(firestore, 'conversations'),
        where('userId', '==', user.uid),
        orderBy('createdAt', 'desc')
      );
      const querySnapshot = await getDocs(q);
      const conversationsList = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setConversations(conversationsList);
    } catch (err) {
      console.error('Error fetching conversations:', err);
      setError('Failed to fetch conversations. Please try again.');
    }
  };

  const handleSubmit = async (type) => {
    if (!input.trim()) return;

    setIsLoading(true);
    setError(null);
    const newUserMessage = { type: 'user', content: input, platform };
    setMessages(prev => [...prev, newUserMessage]);
    setInput('');

    const endpoint = type === 'text' ? '/generate-text' : '/generate-image';
    
    try {
      const token = await auth.currentUser?.getIdToken();
      const response = await axios.post(endpoint, 
        { platform, prompt: input },
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );
      
      const data = response.data;
      
      console.log('AI Model Response:', data);
      
      const newAssistantMessage = {
        type: 'assistant',
        content: type === 'text' ? data.generated_text : 'Here\'s the generated image:',
        image: type === 'image' ? data.generated_image : undefined
      };
      
      const updatedMessages = [...messages, newUserMessage, newAssistantMessage];
      setMessages(updatedMessages);

      // Save conversation to Firestore
      await addDoc(collection(firestore, 'conversations'), {
        userId: auth.currentUser?.uid,
        messages: updatedMessages,
        createdAt: new Date(),
        platform
      });

      fetchConversations();
    } catch (err) {
      console.error('Error:', err);
      let errorMessage = 'An error occurred while generating content. Please try again.';
      if (err.response && err.response.data && err.response.data.error) {
        errorMessage = err.response.data.error;
        if (err.response.data.details) {
          errorMessage += ` Details: ${err.response.data.details}`;
        }
      }
      setError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const startNewConversation = () => {
    setMessages([]);
    setIsSidebarOpen(false);
  };

  const loadConversation = (conversation) => {
    setMessages(conversation.messages);
    setPlatform(conversation.platform || 'LinkedIn');
    setIsSidebarOpen(false);
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-50 w-64 bg-white shadow-lg transform ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 ease-in-out md:relative md:translate-x-0`}>
        <div className="flex justify-between items-center p-4 border-b">
          <h2 className="text-xl font-semibold">Conversations</h2>
          <button onClick={() => setIsSidebarOpen(false)} className="md:hidden">
            <X className="w-6 h-6" />
          </button>
        </div>
        <div className="p-4">
          <button
            onClick={startNewConversation}
            className="w-full py-2 px-4 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition duration-300"
          >
            New Conversation
          </button>
        </div>
        <div className="overflow-y-auto h-full pb-20">
          {conversations.length === 0 ? (
            <p className="text-gray-500 text-center p-4">No conversations yet</p>
          ) : (
            conversations.map((conv) => (
              <div
                key={conv.id}
                onClick={() => loadConversation(conv)}
                className="p-4 border-b hover:bg-gray-100 cursor-pointer transition duration-300"
              >
                <p className="font-medium truncate">{conv.messages[0].content}</p>
                <p className="text-sm text-gray-500">{conv.createdAt.toDate().toLocaleString()}</p>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Main Content */}
      <div className="flex flex-col flex-1 overflow-hidden">
        {/* Header */}
        <header className="bg-white shadow-sm p-4 flex justify-between items-center">
          <button onClick={() => setIsSidebarOpen(true)} className="md:hidden">
            <Menu className="w-6 h-6" />
          </button>
          <h1 className="text-2xl font-bold text-center flex-1">Social Media Post Generator</h1>
        </header>

        {/* Main chat area */}
        <div className="flex-1 overflow-y-auto p-4 space-y-4">
          {messages.map((message, index) => (
            <div key={index} className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}>
              <div className={`max-w-[70%] rounded-lg p-3 ${message.type === 'user' ? 'bg-blue-500 text-white' : 'bg-white'}`}>
                {message.platform && <p className="font-semibold mb-1">{message.platform}</p>}
                <p>{message.content}</p>
                {message.image && <img src={message.image} alt="Generated" className="mt-2 rounded-lg max-w-full h-auto" />}
              </div>
            </div>
          ))}
        </div>

        {/* Input area */}
        <div className="bg-white border-t border-gray-200 p-4">
          <div className="flex space-x-2 mb-2 overflow-x-auto pb-2">
            {platforms.map(p => (
              <button
                key={p}
                onClick={() => setPlatform(p)}
                className={`px-3 py-1 rounded-full text-sm flex-shrink-0 ${
                  platform === p ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'
                }`}
              >
                {p}
              </button>
            ))}
          </div>
          <div className="flex items-center space-x-2">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type your prompt here..."
              className="flex-1 p-2 border rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500"
              onKeyPress={(e) => e.key === 'Enter' && handleSubmit('text')}
            />
            <button
              onClick={() => handleSubmit('text')}
              disabled={isLoading}
              className="bg-blue-500 text-white p-2 rounded-full hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              {isLoading ? <Loader2 className="w-5 h-5 animate-spin" /> : <Send className="w-5 h-5" />}
            </button>
            <button
              onClick={() => handleSubmit('image')}
              disabled={isLoading}
              className="bg-purple-500 text-white p-2 rounded-full hover:bg-purple-600 focus:outline-none focus:ring-2 focus:ring-purple-500"
            >
              {isLoading ? <Loader2 className="w-5 h-5 animate-spin" /> : <Image className="w-5 h-5" />}
            </button>
          </div>
        </div>
      </div>
      {error && <div className="text-red-500 p-4 absolute bottom-0 left-0 right-0 bg-white">{error}</div>}
    </div>
  );
}
